from collections import defaultdict


with open('./biopython/data/dna2.fasta', 'r' ) as f:
    content = f.read()

reads = content.split(">")[1:]
len(reads)


def get_repeats(data, n):
    repeats = dict()
    for s in range(len(data)-n +1):
        matches = list()
        for e in range(s, len(data)-n+1):
            if data[e:e+n] in repeats:
                continue

            if data[s:s+n] == data[e:e+n]:
                matches.append(e)
       
        if len(matches) > 1:
            repeats[data[s:s+n]] = matches
    return repeats


for read in reads:
    max_occ = -1
    max_seq = ""    
    head, data = read.split('\n', 1)
    reps = get_repeats(data.replace("\n", ""), 6)
    for rep, indices in reps.items():
        if len(indices)>max_occ:
            max_occ = len(indices)
            max_seq = rep
    print(head.split(' ', 1)[0], max_seq, max_occ)

